INSERT INTO `events` (`id`, `uuid`, `name`, `slug`, `content`, `featured_image`, `images`, `current_participants`, `max_participants`, `date_from`, `date_to`, `location`, `is_payment_active`, `is_featured`, `order`, `payment`, `meta_title`, `meta_keywords`, `meta_descriptions`, `meta_og_image`, `meta_og_url`, `status`, `created_at`, `updated_at`, `deleted_at`) VALUES
(1, '5beb79d565f3474cb63abb86038d5a58', 'Neonatal Care & Breastfeeding Myths Busted with Renowned Pedestrian', 'neonatal-care-and-breastfeeding-myths-busted-with-renowned-pedestrians', '<p>&nbsp;Join us for an enlightening event on Neonatal Care &amp; Breastfeeding Myths Busted, featuring renowned pediatricians. This session aims to provide new parents and caregivers with valuable insights into the critical aspects of neonatal care, ensuring the health and well-being of newborns. Our expert pediatricians will address common myths surrounding breastfeeding, offering evidence-based information to empower families. Don\'t miss this opportunity to learn from the best in the field, ask questions, and gain confidence in caring for your little ones. Mark your calendars and be part of this informative and supportive event!</p><p><strong>Why Attend?</strong></p><p>Educational: Learn about the latest research and best practices in neonatal care.</p><p>Supportive: Connect with other parents and caregivers, and build a community of support.</p><p>Empowering: Feel more confident and informed in making decisions about your baby\'s health and well-being.</p><p><strong>Key Topics:</strong></p><p>1. What are the benefits of breastfeeding for both the baby and the mother?</p><p>2. What are some common breastfeeding challenges new mothers face, and how can they overcome them?</p><p>3. How can a mother know if her baby is getting enough&nbsp;</p><p>4. What tips do you have for latching correctly?</p><p>5. Are there any foods or drinks that should be avoided while breastfeeding?</p><p>6. When and how should a mother introduce a bottle if she wants to supplement breastfeeding with formula?</p><p>7. How often should a newborn be fed, and what is the ideal feeding schedule?</p><p><strong>Meet Our Speakers:</strong></p><p><strong>Dr. Garima Biswas (PT)</strong></p><p>Consultant Physiotherapist Founder of Omansh Health&nbsp;</p><p>Fitness Obstetrics and Pelvic Floor Rehab Therapist</p><p>Labor and Child Birth Educator</p><p><strong>Dr. Pushpendra Patel</strong></p><p>MBBS, MD, Paediatrics, Fellow NICU (New Delhi)</p><p>Trained in Ped. Nephro AIID (New Delhi)</p><p>Gold Medalist</p><p><strong>How to join</strong></p><p>Join us on instagram (<a href=\"https://www.instagram.com/omansh_health_fitness/\"><span style=\"text-decoration: underline;\">omansh_health_fitness</span></a>)</p><p>Date &amp; Time : 14-07-2024, 6PM&nbsp; (Sunday)&nbsp;</p>', 'https://omansh.org/uploads/01J294KV8R0ZFDBBWKEHY8DG1V.webp', NULL, 1, NULL, '2024-07-14 18:00:00', '2024-07-14 19:00:00', 'Online', 0, 1, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2024-07-08 19:20:36', '2025-11-25 17:11:14', NULL),
(2, '97632f050402475db48cbf5038aca659', 'Pregnancy Care Workshop', 'pregnancy-care-workshop', '<p>Join us for the Pregnancy Care Workshop. Don\'t miss this opportunity to learn from the best in the field, ask questions, and gain confidence in caring for your little ones. Mark your calendars and be part of this informative and supportive event!</p><p><strong>Includes:</strong></p><ol><li>Dietary counseling and planning your diet in pregnancy.</li><li>How to exercise safely in pregnancy.</li><li>Answering your pregnancy queries.</li><li>One complimentary consultation at Dr. Anjali Taneja\'s clinic.</li></ol><p><strong>Meet Our Speakers:</strong></p><p><strong>Dr. Garima Biswas (PT)</strong></p><p>Antenatal &amp;&nbsp; post natal fitness expert</p><p><strong>Dr. Anjali Taneja</strong></p><p>MBBS, MD, FMAS, MRCOG</p><p>Principal consultant OBG</p><p><strong>Dr. Garima Chaudhary</strong></p><p>Clinical nutritionist</p><p>&nbsp;Cloudnine hospital, PPG</p><p><strong>How to join</strong></p><p>Join us on instagram (<a href=\"https://www.instagram.com/omansh_health_fitness/\"><span style=\"text-decoration: underline;\">omansh_health_fitness</span></a>)</p><p>Date &amp; Time : 04-08-2024, 10PM&nbsp; (Sunday)&nbsp;</p>', 'https://omansh.org/uploads/01J4115VG8TA8F8KMF62VDP2X1.png', NULL, 0, NULL, '2024-08-04 10:00:00', '2024-08-04 12:00:34', NULL, 0, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2024-07-30 12:15:09', '2024-07-30 12:31:58', NULL),
(3, '6b31297b45964edd97b3739f35417ac7', 'Discover essential tips to make your delivery smoother and less stressful.', 'discover-essential-tips-to-make-your-delivery-smoother-and-less-stressful.', '<h2>Worried about Labor pain and complications?</h2><p>(Join our child birth Education class to Prepare for your big day with confidence)</p><p><strong>Meet Our Expert</strong></p><p>Get personalized advice and answers to your questions<br>from experts in the field</p><p><figure data-trix-attachment=\"{&quot;contentType&quot;:&quot;image/png&quot;,&quot;filename&quot;:&quot;meet-expert.png&quot;,&quot;filesize&quot;:856887,&quot;height&quot;:580,&quot;href&quot;:&quot;https://omansh.org/uploads/nOkn5747X8OYDjV6Hz4GetXg3osVCqwI5KWbXtni.png&quot;,&quot;url&quot;:&quot;https://omansh.org/uploads/nOkn5747X8OYDjV6Hz4GetXg3osVCqwI5KWbXtni.png&quot;,&quot;width&quot;:924}\" data-trix-content-type=\"image/png\" data-trix-attributes=\"{&quot;presentation&quot;:&quot;gallery&quot;}\" class=\"attachment attachment--preview attachment--png\"><a href=\"https://omansh.org/uploads/nOkn5747X8OYDjV6Hz4GetXg3osVCqwI5KWbXtni.png\"><img src=\"https://omansh.org/uploads/nOkn5747X8OYDjV6Hz4GetXg3osVCqwI5KWbXtni.png\" width=\"924\" height=\"580\"><figcaption class=\"attachment__caption\"><span class=\"attachment__name\">meet-expert.png</span> <span class=\"attachment__size\">836.8 KB</span></figcaption></a></figure>Dr. GARIMA BISWAS (PT)</p><p>Consultant Physiotherapist, Founder of Omansh Health and Fitness, Obstetrics and Pelvic Floor Rehab Therapist, Labor and Child Birth Educator, Antenatal and Post Natal Fitness Expert</p>', 'https://omansh.org/uploads/01J5ZCZF7XSJS87AXBRZKC75B0.png', NULL, 0, NULL, '2024-09-01 16:03:45', '2024-09-01 11:30:00', 'Online', 0, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2024-08-23 17:35:21', '2024-08-23 17:40:16', NULL),
(4, '521f28dbb21a46dfb3b7219e6e65ba4d', 'Workshop on Exercises of Antenatal & Postnatal ', 'workshop-on-exercises-of-antenatal-&-postnatal', '<p><strong>Workshop Highlights</strong></p><ul><li>Basic Anatomy &amp; Physiology</li><li>Trimester-Based Prenatal Exercises</li><li>Pregnancy Discomfort Management</li><li>Pregnancy Yoga</li><li>Pilates During Pregnancy</li><li>Postnatal Assessment</li><li>Labor &amp; Pain Management</li><li>Diastasis Recti Management</li><li>Postpartum &amp; Post-Cesarean Exercises</li><li>Baby Handling &amp; Ball Exercises</li></ul><p><strong>Course Includes:</strong></p><ul><li>International Approved Certificate</li><li>16 Credit Hours</li><li>Comprehensive Study Notes</li><li>Breakfast, Lunch &amp; High Tea</li></ul><p><strong>For Registration Contact: 9029152122</strong></p>', 'https://omansh.org/uploads/01J84XB74QJE366363C5D39J2D.jpg', NULL, 0, NULL, '2024-09-28 00:00:00', '2024-09-29 00:00:00', 'Mumbai', 0, 0, NULL, NULL, 'Workshop on Exercises of Antenatal & Postnatal ', NULL, NULL, NULL, NULL, '1', '2024-09-19 17:30:47', '2024-09-19 17:31:52', NULL),
(5, 'a6c31b9f24fb4980aec29812821db036', 'Specialized Program For Women', 'Specialized Program For Women', '<p><strong><em>In PREMENOPAUSAL &amp; MENOPAUSAL</em></strong></p><h2>Program Highlights</h2><ul><li>Boost bone health</li><li>Improve muscle strength</li><li>Manage hormonal changes</li><li>Enhance mood and energy level</li><li>Prevent bone weakness</li></ul>', 'https://omansh.org/uploads/01JEQABRS6RMSV0J9DQ8FVQ51G.jpg', NULL, 0, NULL, NULL, NULL, 'Plot no 290, In front of Ramprastha green gate no. 1 Sec. 4, Vaishali, Ghaziabad', 0, 0, '1', NULL, NULL, NULL, NULL, NULL, NULL, '1', '2024-12-09 20:12:38', '2024-12-10 11:06:34', NULL),
(6, 'a64cd2b0390c4f399ccb2446522c9aaf', 'Osteoporosis Management Program ', 'Osteoporosis Management Program ', '<p><strong><em>\"Strengthen Your Bones Enhance Your Life!\"</em></strong></p><p>Program Highlights</p><ul><li>Evidence-based exercise plans to improve bone density.</li><li>Nutritional counseling for bone health.</li><li>Posture and balance training to prevent falls.</li><li>Education sessions on managing osteoporosis daily.</li></ul>', 'https://omansh.org/uploads/01JEQBENG9Q417K6T0AN4GVBR4.jpg', NULL, 0, NULL, NULL, NULL, 'Plot no 290, In front of Ramprastha green gate no. 1 Sec. 4, Vaishali, Ghaziabad', 0, 0, '2', NULL, NULL, NULL, NULL, NULL, NULL, '1', '2024-12-10 10:55:39', '2024-12-10 11:06:22', NULL),
(7, '0a1910014b5d4db8bda5f01df3dce05a', 'Are you struggling with Joint Pain in your Early 40\'s?', 'Are you struggling with Joint Pain in your Early 40\'s?', '<p>We have a solution..</p><p><strong>Join our Strength &amp; Conditioning Program&nbsp;</strong></p><ul><li>For better muscle strength&nbsp;</li><li>For early bone degeneration prevention&nbsp;</li></ul>', 'https://omansh.org/uploads/01JEQBXNDGGCKM9P315RAD9WVN.jpg', NULL, 0, NULL, NULL, NULL, 'Plot no 290, In front of Ramprastha green gate no. 1 Sec. 4, Vaishali, Ghaziabad', 0, 0, '3', NULL, NULL, NULL, NULL, NULL, NULL, '1', '2024-12-10 11:06:02', '2024-12-10 11:06:02', NULL),
(8, '3136137251504736bca2ab77f85d12d9', 'Antenatal & Postnatal 2 Day Workshop at Raipur', 'antenatal-and-postnatal-2-day-workshop-at-raipur', '<p>Discover the art of nurturing yourself &amp; baby</p>\n<h3>Workshop Highlights</h3>\n<ul>\n<li>Basic Anatomy &amp; Physiology</li>\n<li>Trimester-Based Prenatal Exercise</li>\n<li>Pregnancy Yoga</li>\n<li>Labor &amp; Pain Management</li>\n<li>Postnatal Assessment</li>\n<li>Diastasis Recti Management</li>\n<li>Postpartum &amp; Post-Cesarean Exercises</li>\n<li>Baby Handling &amp; Ball Exercise</li>\n</ul>\n<p><img src=\"/uploads/uploads/8igmgzGO63iciAaZG80sc1qRYL5TGlJjOuc3jWJw.jpg\" alt=\"\" width=\"200\" height=\"150\">&nbsp;</p>\n<h3>Course Includes</h3>\n<ul>\n<li>International Approved Certificates</li>\n<li>16 Credit Hours</li>\n<li>Comprehensive Study Notes</li>\n<li>Breakfast, Lunch and High Tea</li>\n</ul>\n<p><img src=\"/uploads/uploads/XE5WXBkwo96OyQijULYUDZa74z2D4a8uCeBU22lX.jpg\" alt=\"\" width=\"200\" height=\"150\"></p>', 'https://omansh.org/uploads/01JEWMQWG8Q89M5WZAGX9R31MK.webp', NULL, 0, NULL, '2025-01-25 09:00:37', '2025-01-26 17:00:00', 'Raipur', 0, 0, '1', NULL, NULL, NULL, NULL, NULL, NULL, '1', '2024-12-12 12:16:22', '2025-06-04 16:48:37', NULL),
(9, 'adc0ce922e1e4fb7b9b08aec4acd8ef5', '2 Days Workshop On Exercises Of Antenatal & Postnatal', '2-days-workshop-on-exercise-of-antenatal-&-postnatal', '<p><strong>Discover the art of nurturing yourself &amp; baby with Dr. Garima Biswas</strong></p><p><strong>Workshop Highlights</strong></p><p>1. Antenatal</p><p>2. Postnatal Sessions</p><p>3. Labor Management</p><p>4. Special Management</p><p>Date : 26th &amp; 27th April Jabalpur</p>', 'https://omansh.org/uploads/17_03_2025_Omansh_April_Workshop.png', NULL, 0, NULL, '2025-04-26 18:02:44', '2025-04-27 18:02:48', 'Jabalpur', 0, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2025-03-18 19:33:21', '2025-03-18 19:46:59', NULL),
(10, '2f42b0c2f6334e64a33effd0f8390e6c', '2 Days Workshop On Exercises Of Antenatal & Postnatal With Labour Management', '2-days-workshop-on-exercises-of-antenatal-postnatal-with-labour-management', '<p>Discover the art of nuturing yourself &amp; baby with Dr. Garima Biswas.</p><p><strong>Workshop Highlights :</strong></p><p>1. Basic Anatomy &amp; Physiology</p><p>2. <strong>Trimester-Based Prenatal Exercises</strong></p><p>3. Pregnancy Discomfort Management</p><p>4. Pregnancy Yoga</p><p>5. Pilates During Pregnancy</p><p>6. Postnatal Assessment</p><p>7. Diastasis Recti Management</p><p>8. Postpartum &amp; Post-Cesarean Exercises</p><p>9. Baby Handling &amp; Ball Exercises</p><p>10. Labour Management</p><p>Date :&nbsp;<strong>22nd - 23rd March, Mumbai</strong></p>', 'https://omansh.org/uploads/WhatsApp Image 2025-03-18 at 10.57.20_32172ae0.jpg', NULL, 0, NULL, '2025-03-22 18:13:30', '2025-03-23 18:13:38', 'Mumbai', 0, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2025-03-18 19:48:24', '2025-03-18 19:48:55', NULL),
(11, '7ed8c0acb00a4deb9bb7ae8103f2a22d', '2 DAYS WORKSHOP ON EXERCISES OF ANTENATAL & POSTNATAL, Delhi', '2-days-workshop-on-exercises-of-antenatal-postnatal-delhi-', '<p>Discover the art of nurturing yourself &amp; baby with <strong>Dr. Garima Biswas</strong></p><h3><strong>WORKSHOP HIGHLIGHTS</strong></h3><p><strong>Antenatal</strong></p><p> Basic Anatomy &amp; Physiology<br>&nbsp; Changes During Pregnancy<br>&nbsp; Trimester Based Exercises<br>&nbsp; Exercise Using Ball, Dumbbells &amp; Bands<br>&nbsp; Pregnancy Yoga &amp; Pilates Garbh Sanskar<br>&nbsp; Exercise Prescription and Format</p><p><strong>Labor Management</strong></p><p> Pain Management<br>&nbsp; Breathing Techniques<br>&nbsp; Positioning</p><p><strong>Postnatal Sessions</strong></p><p> Hormonal Changes<br>&nbsp; Sexual Changes<br>&nbsp; Return to Exercises<br>&nbsp; Management of LSCS<br>&nbsp; Management of Normal Labor<br>&nbsp; Management of Pelvic Girdle Pain</p><p><strong>Special Management</strong></p><p> Diastasis Recti Management<br>&nbsp; Postpartum &amp; Post-cesarean Exercises<br>&nbsp; Baby Handling &amp; Ball Exercises</p>', 'https://omansh.org/uploads/19_03_2025_Omansh_Post_Workshop_Delhi.png', NULL, 0, NULL, '2025-07-26 09:00:12', '2025-07-27 12:00:49', 'Delhi', 0, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2025-05-02 17:28:23', '2025-05-02 17:35:53', NULL),
(12, 'beb7cb22d1f746199f791a7ec96c7afb', '2 DAYS WORKSHOP ON EXERCISES OF ANTENATAL & POSTNATAL, Surat', '2-days-workshop-on-exercises-of-antenatal-postnatal-surat', '<p><em>Discover the art of nurturing yourself &amp; baby with</em> <strong>Dr. Garima Biswas</strong></p><h3><strong>WORKSHOP HIGHLIGHTS</strong></h3><p><strong>Antenatal</strong></p><p> Basic Anatomy &amp; Physiology<br>&nbsp; Changes During Pregnancy<br>&nbsp; Trimester Based Exercises<br>&nbsp; Exercise Using Ball, Dumbbells &amp; Bands<br>&nbsp; Pregnancy Yoga &amp; Pilates Garbh Sanskar<br>&nbsp; Exercise Prescription and Format</p><p><strong>Labor Management</strong></p><p> Pain Management<br>&nbsp; Breathing Techniques<br>&nbsp; Positioning</p><p><strong>Postnatal Sessions</strong></p><p> Hormonal Changes<br>&nbsp; Sexual Changes<br>&nbsp; Return to Exercises<br>&nbsp; Management of LSCS<br>&nbsp; Management of Normal Labor<br>&nbsp; Management of Pelvic Girdle Pain</p><p><strong>Special Management</strong></p><p> Diastasis Recti Management<br>&nbsp; Postpartum &amp; Post-cesarean Exercises<br>&nbsp; Baby Handling &amp; Ball Exercises</p><p><strong>Venue</strong>: Burhani Hospital Surat<br> <strong>Time</strong>: 09:00 AM to 05:00 PM</p>', 'https://omansh.org/uploads/11_04_2025_Omansh_Surat.png', NULL, 0, NULL, '2025-05-10 09:00:00', '2025-05-11 17:10:20', 'Burhani Hospital Surat', 0, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '2025-05-02 17:42:44', '2025-05-02 17:42:52', NULL),
(13, '80f4f54eb1c54a06aefefc2cf513f305', '2 DAYS  WORKSHOP ON EXERCISES OF  ANTENATAL & POSTNATAL, Pune', '2-days-workshop-on-exercises-of-antenatal-postnatal-pune', '<p><em>Discover the art of nurturing yourself &amp; baby with</em> <strong>Dr. Garima Biswas</strong></p>\n<h2>WORKSHOP HIGHLIGHTS</h2>\n<h3><strong>Antenatal</strong></h3>\n<p> Basic Anatomy &amp; Physiology</p>\n<p> Changes During Pregnancy</p>\n<p>&nbsp; Trimester Based Exercises<br>&nbsp; Exercise Using Ball, Dumbbells &amp; Bands<br>&nbsp; Pregnancy Yoga &amp; Pilates Garbh Sanskar<br>&nbsp; Exercise Prescription and Format</p>\n<p><img src=\"/uploads/uploads/4x48Joh19cBtDbgbJaeohzhzQP9V6Tgs4CX5M0VX.jpg\" width=\"404\" height=\"303\"></p>\n<h3><strong>Labor Management</strong></h3>\n<p> Pain Management<br>&nbsp; Breathing Techniques<br>&nbsp; Positioning</p>\n<p><img src=\"/uploads/uploads/CYcB6qYWv56gfqbiqbpDuONvrm9ArRz40dSI0m3F.jpg\" width=\"403\" height=\"302\"></p>\n<h3><strong>Postnatal Sessions</strong></h3>\n<p> Hormonal Changes<br>&nbsp; Sexual Changes<br>&nbsp; Return to Exercises<br>&nbsp; Management of LSCS<br>&nbsp; Management of Normal Labor<br>&nbsp; Management of Pelvic Girdle Pain</p>\n<h3><strong>Special Management</strong></h3>\n<p> Diastasis Recti Management<br>&nbsp; Postpartum &amp; Post-cesarean Exercises<br>&nbsp; Baby Handling &amp; Ball Exercises</p>\n<p><strong>DATE : 31ST MAY &amp; 1ST JUNE</strong><br><strong>PUNE</strong></p>\n<div class=\"attachment-gallery attachment-gallery--4\">\n<figure class=\"attachment attachment--preview attachment--jpeg\" data-trix-attachment=\"{&quot;contentType&quot;:&quot;image/jpeg&quot;,&quot;filename&quot;:&quot;WhatsApp Image 2025-06-03 at 10.18.55 AM.jpeg&quot;,&quot;filesize&quot;:313363,&quot;height&quot;:1200,&quot;href&quot;:&quot;https://omansh.org/uploads/jam2q1eeKE1EVtFL6kbXBxBEhyrX7hdaKvGEcfjP.jpg&quot;,&quot;url&quot;:&quot;https://omansh.org/uploads/jam2q1eeKE1EVtFL6kbXBxBEhyrX7hdaKvGEcfjP.jpg&quot;,&quot;width&quot;:1600}\"